const daysEl=document.getElementById("days"),hoursEl=document.getElementById("hours"),minsEl=document.getElementById("mins"),secondsEl=document.getElementById("seconds"),newYears="1 Jan 2022";function countdown(){const newYearsDate=new Date(newYears),currentDate=new Date,totalSeconds=new Date(newYearsDate-currentDate)/1e3,days=Math.floor(totalSeconds/3600/24),hours=Math.floor(totalSeconds/3600)%24,mins=Math.floor(totalSeconds/60)%60,seconds=Math.floor(totalSeconds)%60;daysEl.innerHTML=days,hoursEl.innerHTML=formatTime(hours),minsEl.innerHTML=formatTime(mins),secondsEl.innerHTML=formatTime(seconds),console.log(days,hours,mins,seconds)}function formatTime(time){return time<10?`0${time}`:time}countdown(),setInterval(countdown,1e3);